import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AddEmployee() {
  const [employeeData, setEmployeeData] = useState({
    employeeId: '',
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    password: '',
    role: '',
  });
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    axios.post('http://localhost:8090/employees')
      .then((response) => setEmployees(response.data))
      .catch((err) => console.error(err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData((prevData) => ({ ...prevData, [name]: value }));
  };

  const sendData = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8090/employees/create', employeeData);
      setEmployees((prev) => [...prev, response.data]);
      alert('Employee added successfully!');
      setEmployeeData({ employeeId: '', firstName: '', lastName: '', username: '', email: '', password: '', role: '' });
    } catch (err) {
      console.error(err);
      alert('Failed to add employee.');
    }
  };

  return (
    <div className="wrapper">
      <h2 className="header">Add Employee</h2>
      <form onSubmit={sendData} className="form">
        {['employeeId', 'firstName', 'lastName', 'username', 'email', 'password'].map((field) => (
          <input
            key={field}
            type={field === 'password' ? 'password' : 'text'}
            name={field}
            placeholder={field.replace(/([A-Z])/g, ' $1').trim()}
            value={employeeData[field]}
            onChange={handleChange}
            required
          />
        ))}
        <select name="role" value={employeeData.role} onChange={handleChange} required>
          <option value="">Select Role</option>
          <option value="Vet">Vet</option>
          <option value="Groomer">Groomer</option>
        </select>
        <button type="submit">Add Employee</button>
      </form>

      <div className="table-container">
        <h2>Employee List</h2>
        <table>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {employees.map(({ _id, firstName, lastName, username, email, role }) => (
              <tr key={_id}>
                <td>{firstName}</td>
                <td>{lastName}</td>
                <td>{username}</td>
                <td>{email}</td>
                <td>{role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AddEmployee;
