import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AddEmployee() {
  const [employeeId,setEmployeeId] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [employees, setEmployees] = useState([]);

  // Fetch all employees on component mount
  useEffect(() => {
    axios
      .get('http://localhost:8090/employees') // Assuming this route returns all employees
      .then((response) => {
        setEmployees(response.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []); // Empty dependency array to only fetch once on mount

  // Handle employee form submission
  const sendData = async (e) => {
    e.preventDefault();
    const newEmployee = { firstName, lastName, username, email, password, role };
    
    try {
      const response = await axios.post('http://localhost:8090/employees/create', newEmployee);
      setEmployees((prevEmployees) => [...prevEmployees, response.data]); // Optimistically update state
      alert('Employee added successfully!');
      // Clear form fields after submission
      setEmployeeId('');
      setFirstName('');
      setLastName('');
      setUsername('');
      setEmail('');
      setPassword('');
      setRole('');
    } catch (err) {
      console.error(err);
      alert('Failed to add employee.');
    }
  };

  return (
    <>
      <style>
        {`
          body {
            font-family: 'Roboto', sans-serif;
            background-color: #f9f9f9;
            color: #333;
            margin: 0;
            padding: 0;
          }

          .wrapper {
            margin: 40px auto;
            padding: 30px;
            background-color: #fff;
            border-radius: 12px;
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
            max-width: 600px;
            width: 100%;
          }

          .header {
            font-size: 28px;
            font-weight: 600;
            margin-bottom: 20px;
            color: #333;
          }

          .form {
            display: flex;
            flex-direction: column;
            gap: 20px;
          }

          input, select {
            padding: 15px;
            font-size: 16px;
            border: 1px solid #ddd;
            border-radius: 8px;
            transition: all 0.3s ease;
            width: 100%;
            box-sizing: border-box;
          }

          input:focus, select:focus {
            outline: none;
            border-color: #4CAF50;
            box-shadow: 0 0 5px rgba(76, 175, 80, 0.5);
          }

          button {
            padding: 14px;
            font-size: 16px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 8px;
            cursor: pointer;
            transition: background-color 0.3s ease;
          }

          button:hover {
            background-color: #45a049;
          }

          .table-container {
            margin-top: 40px;
            padding: 20px;
            background-color: white;
            border-radius: 12px;
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
          }

          table {
            width: 100%;
            border-collapse: collapse;
            text-align: left;
          }

          th, td {
            padding: 12px;
            font-size: 16px;
            border-bottom: 1px solid #ddd;
          }

          th {
            background-color: #4CAF50;
            color: white;
          }

          tr:hover {
            background-color: #f1f1f1;
          }

          .password-wrapper {
            display: flex;
            align-items: center;
            position: relative;
          }

          .eye-icon {
            position: absolute;
            right: 10px;
            cursor: pointer;
            background: none;
            border: none;
            color: #888;
          }

          .password-wrapper input {
            padding-right: 40px;
          }
        `}
      </style>

      <div className="wrapper">
        <div className="header">Add Employee</div>
        <form onSubmit={sendData} className="form">
        <input 
            type="text" 
            placeholder="Employee ID" 
            required 
            value={firstName} 
            onChange={(e) => setEmployeeId(e.target.value)} 
          />
          <input 
            type="text" 
            placeholder="First Name" 
            required 
            value={firstName} 
            onChange={(e) => setFirstName(e.target.value)} 
          />
          <input 
            type="text" 
            placeholder="Last Name" 
            required 
            value={lastName} 
            onChange={(e) => setLastName(e.target.value)} 
          />
          <input 
            type="text" 
            placeholder="Username" 
            required 
            value={username} 
            onChange={(e) => setUsername(e.target.value)} 
          />
          <input 
            type="email" 
            placeholder="Email" 
            required 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
          />
          <input 
            type="password" 
            placeholder="Password" 
            required 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="">Select Role</option>
            <option value="Vet">Vet</option>
            <option value="Groomer">Groomer</option>
          </select>
          <button type="submit">Add Employee</button>
        </form>
      </div>

      <div className="table-container">
        <h2>Employee List</h2>
        <table>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.firstName}</td>
                <td>{employee.lastName}</td>
                <td>{employee.username}</td>
                <td>{employee.email}</td>
                <td>{employee.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default AddEmployee;
