import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:8090/user", { withCredentials: true })
      .then((response) => {
        setUser(response.data);
        setLoading(false);
      })
      .catch((err) => {
        setError("Failed to fetch user data");
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="container py-5">
      <div className="card shadow p-4 text-center">
        <img src={user.image} alt={user.displayName} className="rounded-circle mb-3" width={120} height={120} />
        <h2>{user.displayName}</h2>
        <p>Email: {user.gmail}</p>
        <p>First Name: {user.firstName}</p>
        <p>Last Name: {user.lastName}</p>
        <button className="btn btn-danger" onClick={() => window.location.href = "/logout"}>Logout</button>
      </div>
    </div>
  );
};

export default Profile;
