import React, { useEffect } from "react";

export default function Chatbot() {
  useEffect(() => {
    // Dynamically load the Botpress script
    const script = document.createElement("script");
    script.src = "https://cdn.botpress.cloud/webchat/v2.2/inject.js";
    script.async = true;

    script.onload = () => {
      // Initialize Botpress once the script is loaded
      window.botpress.on("webchat:ready", () => {
        window.botpress.open();
      });

      // Define the Botpress configuration
      const botConfig = {
        botId: "91e540df-d561-4cd8-8d87-36414840b19a", // Replace with your Bot ID
        configuration: {
          website: "solid",
          email: "solid",
          phone: "778",
          termsOfService: "solid",
          privacyPolicy: "solid",
          color: "#3B82F6",
          variant: "solid",
          themeMode: "light",
          fontFamily: "inter",
          radius: 1,
        },
        clientId: "269a73fc-4318-4c03-82b7-7bffc61288b2", // Replace with your Client ID
      };

      // Initialize the chatbot with the provided configuration
      window.botpress.init(botConfig);
    };

    // Append the Botpress script to the document body
    document.body.appendChild(script);

    // Cleanup the script when the component unmounts
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="webchat" style={{ height: "600px", width: "400px" }}>
      <iframe
        style={{ height: "100%", width: "100%", border: "none" }}
        src="https://files.bpcontent.cloud/2025/02/06/17/20250206172318-P0CQ3EFJ.js" // Placeholder URL for the iframe
        title="Chatbot"
      ></iframe>
    </div>
  );
}
