// src/components/EmployeeList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [formData, setFormData] = useState({ name: '', email: '', password: '', role: 'Vet' });

    useEffect(() => {
        axios.get('/api/employees')
            .then(res => setEmployees(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('/api/employees/create', formData);
            setEmployees([...employees, res.data]);
            setFormData({ name: '', email: '', password: '', role: 'Vet' });
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2 style={{ color: 'navy' }}>Employee Management</h2>
            <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
                <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required style={{ margin: '5px' }} />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required style={{ margin: '5px' }} />
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required style={{ margin: '5px' }} />
                <select name="role" value={formData.role} onChange={handleChange} style={{ margin: '5px' }}>
                    <option value="Vet">Vet</option>
                    <option value="Groomer">Groomer</option>
                    <option value="Admin">Admin</option>
                </select>
                <button type="submit" style={{ margin: '5px', padding: '5px' }}>Add Employee</button>
            </form>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(emp => (
                        <tr key={emp._id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EmployeeList;
