import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AddEmployee() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [employees, setEmployees] = useState([]);
  const [passwordVisible, setPasswordVisible] = useState(false); // State to toggle password visibility

  // Fetch all employees on component mount
  useEffect(() => {
    axios
      .get('http://localhost:8090/employees') // Assuming this route returns all employees
      .then((response) => {
        setEmployees(response.data); // Set the fetched employee data
      })
      .catch((err) => {
        console.error(err);
      });
  }, [employees]); // Re-run when the employee list is updated

  // Handle employee form submission
  const sendData = async (e) => {
    e.preventDefault();
    const newEmployee = { firstName, lastName, username, email, password, role };
    
    try {
      await axios.post('http://localhost:8090/employees/create', newEmployee); // Post to backend to create employee
      alert('Employee added successfully!');
      
      // After creating the employee, refresh the list
      const response = await axios.get('http://localhost:8090/employees');
      setEmployees(response.data); // Update employee list after adding
    } catch (err) {
      console.error(err);
      alert('Failed to add employee.');
    }
  };

  return (
    <>
      <style>
        {`
          .wrapper {
            margin-top: 20px;
            padding: 20px;
            background-color: #f5f5f5;
            border-radius: 10px;
          }

          .header {
            font-size: 24px;
            margin-bottom: 20px;
            font-weight: bold;
          }

          .form {
            display: flex;
            flex-direction: column;
            gap: 15px;
          }

          input {
            padding: 10px;
            font-size: 14px;
            border-radius: 5px;
            border: 1px solid #ccc;
            margin: 5px 0;
          }

          button {
            padding: 12px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            font-size: 16px;
          }

          button:hover {
            background-color: #45a049;
          }

          .table-container {
            margin-top: 40px;
            padding: 20px;
            background-color: white;
            border-radius: 10px;
            box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
          }

          table {
            width: 100%;
            border-collapse: collapse;
          }

          th, td {
            padding: 12px;
            text-align: left;
            border-bottom: 1px solid #ddd;
          }

          th {
            background-color: #5c6bc0;
            color: white;
          }

          tr:hover {
            background-color: #f1f1f1;
          }

          .password-wrapper {
            display: flex;
            align-items: center;
          }

          .eye-icon {
            cursor: pointer;
            margin-left: -30px;
            background: none;
            border: none;
          }
        `}
      </style>
      
      <div className="wrapper">
        <div className="header">Add Employee</div>
        <form onSubmit={sendData} className="form">
          <input 
            type="text" 
            placeholder="First Name" 
            required 
            onChange={(e) => setFirstName(e.target.value)} 
          />
          <input 
            type="text" 
            placeholder="Last Name" 
            required 
            onChange={(e) => setLastName(e.target.value)} 
          />
          <input 
            type="text" 
            placeholder="Username" 
            required 
            onChange={(e) => setUsername(e.target.value)} 
          />
          <input 
            type="email" 
            placeholder="Email" 
            required 
            onChange={(e) => setEmail(e.target.value)} 
          />
          
          {/* Password Field with Eye Icon */}
          <div className="password-wrapper">
            <input 
              type={passwordVisible ? "text" : "password"} 
              placeholder="Password" 
              required 
              onChange={(e) => setPassword(e.target.value)} 
            />
            <button 
              type="button" 
              className="eye-icon" 
              onClick={() => setPasswordVisible(!passwordVisible)}
            >
              {passwordVisible ? 'üôà' : 'üëÅÔ∏è'} {/* Eye icon */}
            </button>
          </div>

          <select onChange={(e) => setRole(e.target.value)} required>
            <option value="">Select Role</option>
            <option value="Vet">Vet</option>
            <option value="Groomer">Groomer</option>
          </select>
          <button type="submit">Add Employee</button>
        </form>
      </div>

      <div className="table-container">
        <h2>Employee List</h2>
        <table>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee.firstName}</td>
                <td>{employee.lastName}</td>
                <td>{employee.username}</td>
                <td>{employee.email}</td>
                <td>{employee.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default AddEmployee;
