import React, { useState } from 'react'; 
import { Webchat, WebchatProvider, Fab, useClient } from '@botpress/webchat';  // Ensure proper import

const clientId = "269a73fc-4318-4c03-82b7-7bffc61288b2";

const configuration = { color: '#000' };

export default function Chatbot() {
  const client = useClient(clientId);  // Ensure it's used inside a component

  const [isWebchatOpen, setIsWebchatOpen] = useState(false);  // Use state within component

  const toggleWebchat = () => {
    setIsWebchatOpen(prevState => !prevState);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <WebchatProvider client={client} configuration={configuration}>
        <Fab onClick={toggleWebchat} />
        <div
          style={{
            display: isWebchatOpen ? 'block' : 'none',
          }}
        >
          <Webchat />
        </div>
      </WebchatProvider>
    </div>
  );
}
