import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddEmployee = () => {
    const [employees, setEmployees] = useState([]);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        username: '',
        email: '',
        password: '',
        role: 'Vet' // Default role is Vet
    });
    const [isEditing, setIsEditing] = useState(false); // Track if we're editing an employee
    const [currentEmployeeId, setCurrentEmployeeId] = useState(null); // ID of the employee we're editing

    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('/api/employees');
            setEmployees(response.data);
        } catch (error) {
            console.error("Error fetching employees", error);
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                // Update employee
                await axios.put(`/api/employees/${currentEmployeeId}`, formData);
                setIsEditing(false); // Reset edit mode
                setCurrentEmployeeId(null); // Clear current employee ID
            } else {
                // Create new employee
                await axios.post('/api/employees/create', formData);
            }
            fetchEmployees(); // Refresh employee list
            setFormData({ firstName: '', lastName: '', username: '', email: '', password: '', role: 'Vet' }); // Reset form
        } catch (error) {
            console.error("Error adding or updating employee", error);
        }
    };

    const handleEdit = (employee) => {
        setFormData({
            firstName: employee.firstName,
            lastName: employee.lastName,
            username: employee.username,
            email: employee.email,
            password: '', // Optional, don't auto-fill the password
            role: employee.role
        });
        setIsEditing(true);
        setCurrentEmployeeId(employee._id);
    };

    return (
        <div style={containerStyle}>
            <h2 style={titleStyle}>{isEditing ? "Edit Employee" : "Add Employee"}</h2>
            <form onSubmit={handleSubmit} style={formStyle}>
                <input type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleChange} required style={inputStyle} />
                <input type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleChange} required style={inputStyle} />
                <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required style={inputStyle} />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required style={inputStyle} />
                <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} style={inputStyle} />

                <label style={labelStyle}>Role:</label>
                <select name="role" value={formData.role} onChange={handleChange} style={selectStyle}>
                    <option value="Vet">Vet</option>
                    <option value="Groomer">Groomer</option>
                </select>

                <button type="submit" style={buttonStyle}>{isEditing ? "Update Employee" : "Add Employee"}</button>
            </form>

            <h3 style={subtitleStyle}>Employee List</h3>
            <table style={tableStyle}>
                <thead>
                    <tr style={headerRowStyle}>
                        <th style={thStyle}>First Name</th>
                        <th style={thStyle}>Last Name</th>
                        <th style={thStyle}>Username</th>
                        <th style={thStyle}>Email</th>
                        <th style={thStyle}>Role</th>
                        <th style={thStyle}>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(emp => (
                        <tr key={emp._id}>
                            <td style={tdStyle}>{emp.firstName}</td>
                            <td style={tdStyle}>{emp.lastName}</td>
                            <td style={tdStyle}>{emp.username}</td>
                            <td style={tdStyle}>{emp.email}</td>
                            <td style={tdStyle}>{emp.role}</td>
                            <td style={tdStyle}>
                                <button onClick={() => handleEdit(emp)} style={editButtonStyle}>Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

// Styles
const containerStyle = {
    maxWidth: "600px",
    margin: "auto",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    backgroundColor: "#fff"
};

const titleStyle = { textAlign: "center", color: "#333", fontSize: "24px", fontWeight: "bold" };
const subtitleStyle = { textAlign: "center", marginTop: "20px", fontSize: "18px", fontWeight: "bold" };
const formStyle = { display: "flex", flexDirection: "column", gap: "10px" };
const inputStyle = { padding: "10px", fontSize: "16px", borderRadius: "5px", border: "1px solid #ccc" };
const selectStyle = { padding: "10px", fontSize: "16px", borderRadius: "5px", border: "1px solid #ccc" };
const buttonStyle = { padding: "10px", fontSize: "16px", backgroundColor: "#28a745", color: "#fff", border: "none", borderRadius: "5px", cursor: "pointer" };
const labelStyle = { fontSize: "16px", fontWeight: "bold" };
const tableStyle = { width: "100%", borderCollapse: "collapse", marginTop: "10px" };
const headerRowStyle = { backgroundColor: "#f4f4f4" };
const thStyle = { padding: "10px", border: "1px solid #ddd", textAlign: "left" };
const tdStyle = { padding: "10px", border: "1px solid #ddd" };
const editButtonStyle = { backgroundColor: "#007bff", color: "#fff", padding: "5px 10px", border: "none", borderRadius: "5px", cursor: "pointer" };

export default AddEmployee;
