import React, { useState, useEffect } from "react";
import axios from "axios";

function AddEmployee() {
  const [employeeId, setEmployeeId] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [employees, setEmployees] = useState([]); // Store employee list


   // Fetch employees when component loads
   useEffect(() => {
    fetchEmployees();
  }, []);

  async function fetchEmployees() {
    try {
      const response = await axios.get("http://localhost:8090/employees/"); // Make sure this matches your route for fetching employees
      setEmployees(response.data);  // Update employee list state
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();

    //Validate form
    if (!employeeId || !firstName || !lastName || !username || !email || !password || !role) {
      alert("All fields are required.");
      return;
    }

    const newEmployee = {
      employeeId,
      firstName,
      lastName,
      username,
      email,
      password,
      role,
    };

    try {
      await axios.post("http://localhost:8090/employee/create", newEmployee);
      alert("Employee added successfully!");
      // Clear form
      setEmployeeId("");
      setFirstName("");
      setLastName("");
      setUsername("");
      setEmail("");
      setPassword("");
      setRole("");

       // Refresh employee list
       fetchEmployees();

    } catch (error) {
      alert(`Error: ${error.response?.data?.error || error.message}`);
    }
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center">Add Employee</h2>
      <form onSubmit={handleSubmit} className="p-4 shadow rounded bg-light">
        <div className="mb-3">
          <label className="form-label">Employee ID</label>
          <input
            type="text"
            className="form-control"
            value={employeeId}
            required
            onChange={(e) => setEmployeeId(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">First Name</label>
          <input
            type="text"
            className="form-control"
            value={firstName}
            required
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Last Name</label>
          <input
            type="text"
            className="form-control"
            value={lastName}
            required
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Username</label>
          <input
            type="text"
            className="form-control"
            value={username}
            required
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Role</label>
          <select
            className="form-control"
            value={role}
            required
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="">Select Role</option>
            <option value="Vet">Vet</option>
            <option value="Groomer">Groomer</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary w-100">
          Add Employee
        </button>
      </form>
      <div className="table-container mt-5">
      <h2>Employee List</h2>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Employee ID</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
              <th>Email</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {employees.length > 0 ? (
              employees.map((employee) => (
                <tr key={employee._id}>
                  <td>{employee.employeeId}</td>
                  <td>{employee.firstName}</td>
                  <td>{employee.lastName}</td>
                  <td>{employee.username}</td>
                  <td>{employee.email}</td>
                  <td>{employee.role}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6" className="text-center">No employees found</td>
              </tr>
            )}
          </tbody>
        </table>

      </div>

    </div>
  );
}

export default AddEmployee;
